var whereAt=function(){if(window.pageXOffset!=undefined){return function(e){return[e.clientX+window.pageXOffset,e.clientY+window.pageYOffset]}}else return function(){var e=window.event,t=document.documentElement,n=document.body;return[e.clientX+t.scrollLeft+n.scrollLeft,e.clientY+t.scrollTop+n.scrollTop]}}()